import React from "react"

class Hamburger extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			isOpen: false,
			iconName: "fa fa-bars fa-2x"
		}
		this.HamburgerToggle = this.HamburgerToggle.bind(this)
	}

	HamburgerToggle() {
		this.setState(prevState => {
			this.state.isOpen=!prevState.isOpen
		})
		if(this.state.isOpen === true) {
			this.setState(() => {
				this.state.iconName = "far fa-window-close"
			})
		} else {
			this.setState(() => {
				this.state.iconName = "fa fa-bars fa-2x"
			})
		}
	}

    render() {
        return (
            <div>
            	<i className="{this.state.iconName} hamburgerComponent" onClick={this.HamburgerToggle}></i>
            	<i className="far fa-window-close"></i>
            </div>
        )
    }
}

export default Hamburger
