import React from "react"
import projectsData from "./projectsData"
import ChaletsEtCaviar from "./img/ChaletsEtCaviar.jpg"
import Gougeres from "./img/Gougeres.jpg"
import OutdoorFilmFest from "./img/OutdoorFilmFest.jpg"
import ProfessionalPortfolio from "./img/ProfessionalPortfolio.jpg"

class Project extends React.Component {
    render() {
        return (
            <div className="container project-showcase">
                <img src="./img/OutdoorFilmFest.jpg" />
            	<img src={this.props.image} />
            	<h3>{this.props.name}</h3>
                <p>{this.props.description}</p>
                <a href={this.props.link} className="project-links" target="_blank">View Project</a>
            </div>
        )
    }
}

export default Project
