import React from "react"

class Hamburger extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			isOpen: false,
		}
		this.HamburgerToggle = this.HamburgerToggle.bind(this)
	}

	HamburgerToggle() {
		this.setState((prevState) => {
			this.state.isOpen=!prevState.isOpen
		})
		
		if(this.state.isOpen != false) {
			return(
				<div><i className="far fa-window-close"></i></div>
			)
		} else {
			return(
				<div><i className="fa fa-bars fa-2x hamburgerComponent" onClick={this.HamburgerToggle}></i></div>
			)
		}
	}

    render() {
        return (
            <div>
            	<i className="fa fa-bars fa-2x hamburgerComponent" onClick={this.HamburgerToggle}></i>
            	<i className="far fa-window-close"></i>
            </div>
        )
    }
}

export default Hamburger
