import React from "react"

class Hamburger extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			isOpen: false,
		}
		this.HamburgerToggle = this.HamburgerToggle.bind(this)
	}


	HamburgerToggle() {
		this.setState((prevState) => {
			this.state.isOpen = !prevState.isOpen
		})
	}

    render() {
    	const iconClassName = this.state.isOpen ? "far fa-window-close hamburgerComponent" : "fa fa-bars fa-2x hamburgerComponent"
        return (
            <div>
            	<i className={{iconClassName}} onClick={this.HamburgerToggle}></i>
            </div>
        )
    }
}

export default Hamburger
